import Fonts from './fonts';
import Metrics from './metrics';
import Colors from './colors';

// This file is for a reusable grouping of Theme items.
// Similar to an XML fragment layout in Android

const ApplicationStyles = {
  screen: {
    container: {
      flex: 1,
      padding: 20,
      backgroundColor: Colors.transparent,
    },
    section: {
      margin: Metrics.section,
      padding: Metrics.baseMargin,
    },
    sectionText: {
      ...Fonts.style.normal,
      paddingVertical: Metrics.doubleBaseMargin,
      color: Colors.white,
      marginVertical: Metrics.smallMargin,
      textAlign: 'center',
    },
    subtitle: {
      color: Colors.white,
      padding: Metrics.smallMargin,
      marginBottom: Metrics.smallMargin,
      marginHorizontal: Metrics.smallMargin,
    },
    titleText: {
      ...Fonts.style.h2,
      fontSize: 14,
      color: Colors.text,
    },
    loading: {
      flex: 1,
      ...Fonts.style.h5,
      alignItems: 'center',
      textAlign: 'center',
    },
    button: {
      height: 36,
      backgroundColor: Colors.jhipsterBlue,
      borderColor: Colors.jhipsterBlue,
      borderWidth: 1,
      borderRadius: 8,
      alignSelf: 'stretch',
      justifyContent: 'center',
    },
    buttonText: {
      fontSize: 18,
      color: 'white',
      alignSelf: 'center',
    },
    errorText: {
      color: Colors.error,
      fontSize: 14,
    },
    successText: {
      color: 'green',
      fontSize: 14,
    },
    darkLabel: {
      fontWeight: 'bold',
      color: Colors.white,
    },
    label: {
      fontWeight: 'bold',
    },
    flex: {
      flex: 1,
    },
    paddedScrollView: { paddingBottom: 30 },
  },
  entity: {
    listRow: {
      flex: 1,
      padding: 10,
      borderRadius: 7,
      backgroundColor: Colors.jhipsterBlue,
      marginVertical: Metrics.smallMargin,
      justifyContent: 'center',
    },
    whiteLabel: {
      color: 'white',
      fontWeight: 'bold',
    },
    listContent: {
      marginTop: Metrics.baseMargin,
    },
    entityButtons: { marginBottom: 20 },
    imageBlob: {
      width: '50%',
      height: undefined,
      aspectRatio: 1,
      resizeMode: 'cover',
      borderWidth: 1,
      borderColor: 'lightgrey',
    },
  },
  entityDeleteModal: {
    centeredView: {
      flex: 1,
      justifyContent: 'center',
      alignItems: 'center',
      marginTop: 22,
    },
    modalView: {
      margin: 20,
      backgroundColor: 'white',
      borderRadius: 20,
      padding: 35,
      alignItems: 'center',
      shadowColor: '#000',
      shadowOffset: {
        width: 0,
        height: 2,
      },
      shadowOpacity: 0.25,
      shadowRadius: 3.84,
      elevation: 5,
      maxHeight: 300,
    },
    openButton: {
      backgroundColor: 'grey',
      borderRadius: 15,
      padding: 10,
      elevation: 2,
    },
    cancelButton: {
      backgroundColor: 'grey',
    },
    submitButton: {
      backgroundColor: 'red',
    },
    textStyle: {
      color: 'white',
      fontWeight: 'bold',
      textAlign: 'center',
    },
    modalText: {
      marginBottom: 15,
      textAlign: 'center',
    },
    flexRow: {
      flexDirection: 'row',
    },
  },
};

export default ApplicationStyles;
