import React, { PureComponent } from 'react'
import PropTypes from 'prop-types'
import {
  View,
  SafeAreaView,
  TouchableOpacity,
  TouchableNativeFeedback,
  TouchableWithoutFeedback,
  Text,
  TextInput,
  Platform,
  Modal,
  FlatList,
  StyleSheet,
  ActivityIndicator
} from 'react-native'
import isEqual from 'lodash.isequal'
import memoize from 'memoize-one'
import { RowItem } from './components'
import { callIfFunction } from './helpers'

const Touchable =
  Platform.OS === 'android' ? TouchableNativeFeedback : TouchableOpacity

const defaultStyles = {
  container: {},
  modalWrapper: {},
  selectToggle: {
    marginTop: 5,
    marginBottom: 15,
    paddingHorizontal: 10,
    paddingVertical: 12,
    borderRadius: 4
  },
  selectToggleText: {},
  item: {},
  subItem: {},
  itemText: {
    fontSize: 17
  },
  selectedItemText: {},
  selectedSubItemText: {},
  subItemText: {
    fontSize: 15,
    paddingLeft: 8
  },
  searchBar: {
    backgroundColor: '#f8f8f8',
    flexDirection: 'row'
  },
  center: {
    alignItems: 'center',
    justifyContent: 'center'
  },
  separator: {},
  subSeparator: {
    height: 0
  },
  chipsWrapper: {},
  chipContainer: {},
  chipText: {},
  chipIcon: {},
  searchTextInput: {},
  scrollView: {},
  button: {},
  cancelButton: {},
  confirmText: {},
  toggleIcon: {},
  selectedItem: {},
  selectedSubItem: {}
}

const defaultColors = {
  primary: '#3f51b5',
  success: '#4caf50',
  cancel: '#1A1A1A',
  text: '#2e2e2e',
  subText: '#848787',
  selectToggleTextColor: '#333',
  searchPlaceholderTextColor: '#999',
  searchSelectionColor: 'rgba(0,0,0,0.2)',
  chipColor: '#848787',
  itemBackground: '#fff',
  subItemBackground: '#ffffff',
  disabled: '#d7d7d7'
}

const ComponentContainer = ({ children }) => (
  <View
    style={{ marginTop: 20, alignItems: 'center', justifyContent: 'center' }}
  >
    {children}
  </View>
)

const noResults = (
  <ComponentContainer>
    <Text>Sorry, no results</Text>
  </ComponentContainer>
)

const noItems = (
  <ComponentContainer>
    <Text>Sorry, no items</Text>
  </ComponentContainer>
)

const loadingComp = (
  <ComponentContainer>
    <ActivityIndicator />
  </ComponentContainer>
)

// let date = new Date()
class SectionedMultiSelect extends PureComponent {
  static propTypes = {
    single: PropTypes.bool,
    selectedItems: PropTypes.array,
    items: PropTypes.array,
    displayKey: PropTypes.string,
    uniqueKey: PropTypes.string.isRequired,
    subKey: PropTypes.string,
    onSelectedItemsChange: PropTypes.func.isRequired,
    showDropDowns: PropTypes.bool,
    showChips: PropTypes.bool,
    readOnlyHeadings: PropTypes.bool,
    selectText: PropTypes.string,
    selectedText: PropTypes.oneOfType([PropTypes.string, PropTypes.func]),
    renderSelectText: PropTypes.func,
    confirmText: PropTypes.string,
    hideConfirm: PropTypes.bool,
    styles: PropTypes.oneOfType([PropTypes.array, PropTypes.object]),
    colors: PropTypes.objectOf(PropTypes.string),
    searchPlaceholderText: PropTypes.string,
    noResultsComponent: PropTypes.oneOfType([PropTypes.func, PropTypes.object]),
    loadingComponent: PropTypes.oneOfType([PropTypes.func, PropTypes.object]),
    loading: PropTypes.bool,
    subItemFontFamily: PropTypes.object,
    itemFontFamily: PropTypes.object,
    searchTextFontFamily: PropTypes.object,
    confirmFontFamily: PropTypes.object,
    showRemoveAll: PropTypes.bool,
    removeAllText: PropTypes.string,
    modalSupportedOrientations: PropTypes.arrayOf(PropTypes.string),
    modalAnimationType: PropTypes.string,
    modalWithSafeAreaView: PropTypes.bool,
    modalWithTouchable: PropTypes.bool,
    hideSearch: PropTypes.bool,
    footerComponent: PropTypes.oneOfType([PropTypes.func, PropTypes.object]),
    stickyFooterComponent: PropTypes.oneOfType([
      PropTypes.func,
      PropTypes.object
    ]),
    selectToggleIconComponent: PropTypes.oneOfType([
      PropTypes.func,
      PropTypes.object
    ]),
    cancelIconComponent: PropTypes.oneOfType([
      PropTypes.func,
      PropTypes.object
    ]),
    searchIconComponent: PropTypes.oneOfType([
      PropTypes.func,
      PropTypes.object
    ]),
    selectedIconComponent: PropTypes.oneOfType([
      PropTypes.func,
      PropTypes.object
    ]),
    unselectedIconComponent: PropTypes.oneOfType([
      PropTypes.func,
      PropTypes.object
    ]),
    dropDownToggleIconUpComponent: PropTypes.oneOfType([
      PropTypes.func,
      PropTypes.object
    ]),
    dropDownToggleIconDownComponent: PropTypes.oneOfType([
      PropTypes.func,
      PropTypes.object
    ]),
    chipRemoveIconComponent: PropTypes.oneOfType([
      PropTypes.func,
      PropTypes.object
    ]),
    selectChildren: PropTypes.bool,
    highlightChildren: PropTypes.bool,
    onSelectedItemObjectsChange: PropTypes.func,
    itemNumberOfLines: PropTypes.number,
    selectLabelNumberOfLines: PropTypes.number,
    showCancelButton: PropTypes.bool,
    hideSelect: PropTypes.bool,
    onConfirm: PropTypes.func,
    onCancel: PropTypes.func,
    headerComponent: PropTypes.oneOfType([PropTypes.func, PropTypes.object]),
    alwaysShowSelectText: PropTypes.bool,
    searchAdornment: PropTypes.func,
    expandDropDowns: PropTypes.bool,
    animateDropDowns: PropTypes.bool,
    customLayoutAnimation: PropTypes.object,
    onChangeSearchText: PropTypes.func,
    filterItems: PropTypes.func,
    onToggleSelector: PropTypes.func,
    noItemsComponent: PropTypes.oneOfType([PropTypes.func, PropTypes.object]),
    customChipsRenderer: PropTypes.func,
    chipsPosition: PropTypes.oneOf(['top', 'bottom']),
    hideChipRemove: PropTypes.bool,
    autoFocus: PropTypes.bool,
    iconKey: PropTypes.string,
    disabled: PropTypes.bool,
    selectedIconOnLeft: PropTypes.bool,
    parentChipsRemoveChildren: PropTypes.bool,
    IconRenderer: PropTypes.oneOfType([PropTypes.func, PropTypes.object])
      .isRequired,
    itemsFlatListProps: PropTypes.object,
    subItemsFlatListProps: PropTypes.object,
    icons: PropTypes.object
  }

  static defaultProps = {
    single: false,
    selectedItems: [],
    displayKey: 'name',
    showDropDowns: true,
    showChips: true,
    readOnlyHeadings: false,
    selectText: 'Select',
    selectedText: 'selected',
    confirmText: 'Confirm',
    hideConfirm: false,
    searchPlaceholderText: 'Search categories...',
    noResultsComponent: noResults,
    loadingComponent: loadingComp,
    loading: false,
    styles: {},
    colors: {},
    icons: {
      search: {
        name: 'search',
        size: 24
      },
      arrowUp: {
        name: 'keyboard-arrow-up',
        size: 22
      },
      arrowDown: {
        name: 'keyboard-arrow-down',
        size: 22
      },
      selectArrowDown: {
        name: 'keyboard-arrow-down',
        size: 24
      },
      close: {
        name: 'close',
        size: 16
      },
      check: {
        name: 'check',
        size: 16
      },
      cancel: {
        name: 'cancel',
        size: 18
      }
    },
    itemFontFamily: {
      fontFamily: Platform.OS === 'android' ? 'normal' : 'Avenir',
      fontWeight: 'bold'
    },
    subItemFontFamily: {
      fontFamily: Platform.OS === 'android' ? 'normal' : 'Avenir',
      fontWeight: '200'
    },
    searchTextFontFamily: {
      fontFamily: Platform.OS === 'android' ? 'normal' : 'Avenir',
      fontWeight: '200'
    },
    confirmFontFamily: {
      fontFamily: Platform.OS === 'android' ? 'normal' : 'Avenir',
      fontWeight: 'bold'
    },
    removeAllText: 'Remove all',
    showRemoveAll: false,
    modalSupportedOrientations: ['portrait', 'landscape'],
    modalAnimationType: 'fade',
    modalWithSafeAreaView: false,
    modalWithTouchable: false,
    hideSearch: false,
    selectChildren: false,
    highlightChildren: false,
    itemNumberOfLines: null,
    selectLabelNumberOfLines: 1,
    showCancelButton: false,
    hideSelect: false,
    alwaysShowSelectText: false,
    expandDropDowns: false,
    animateDropDowns: true,
    filterItems: null,
    noItemsComponent: noItems,
    chipsPosition: 'bottom',
    hideChipRemove: false,
    autoFocus: false,
    disabled: false,
    selectedIconOnLeft: false,
    parentChipsRemoveChildren: false,
    itemsFlatListProps: {},
    subItemsFlatListProps: {}
  }

  constructor(props) {
    super(props)

    if (!props.IconRenderer) {
      throw new Error(`react-native-sectioned-multi-select: You must supply an IconRenderer prop.
        E.g import Icon from "react-native-vector-icons/MaterialIcons"
        <SectionedMultiSelect IconRenderer={Icon}/>
      `)
    }

    this.IconRenderer = props.IconRenderer

    this.state = {
      selector: false,
      searchTerm: '',
      highlightedChildren: []
    }
  }

  getStyles = memoize(
    (styles) => StyleSheet.flatten([defaultStyles, styles]),
    isEqual
  )
  getColors = memoize(
    (colors) => StyleSheet.flatten([defaultColors, colors]),
    isEqual
  )

  // componentWillUpdate() { date = new Date();}
  // componentDidUpdate() {console.log(new Date().valueOf() - date.valueOf())}

  getProp = (object, key) => object && object[key]

  rejectProp = (items, fn) => items.filter(fn)

  find = (id, items) => {
    if (!items) {
      return undefined
    }
    const { uniqueKey, subKey } = this.props
    let i = 0
    let found
    for (; i < items.length; i += 1) {
      if (items[i][uniqueKey] === id) {
        return items[i]
      } else if (Array.isArray(items[i][subKey])) {
        found = this.find(id, items[i][subKey])
        if (found) {
          return found
        }
      }
    }
  }

  reduceSelected = (array, toSplice) => {
    const { uniqueKey } = this.props
    array.reduce((prev, curr) => {
      toSplice.includes(curr[uniqueKey]) &&
        toSplice.splice(
          toSplice.findIndex((el) => el === curr[uniqueKey]),
          1
        )
    }, {})
    return toSplice
  }

  _getSelectLabel = () => {
    const {
      selectText,
      selectedText,
      single,
      selectedItems,
      displayKey,
      alwaysShowSelectText,
      renderSelectText,
      selectLabelNumberOfLines
    } = this.props

    const styles = this.getStyles(this.props.styles)
    const colors = this.getColors(this.props.colors)

    let customSelect = null

    if (renderSelectText) {
      customSelect = renderSelectText(this.props)
      if (typeof customSelect !== 'string') {
        return customSelect
      }
    }

    let label = `${selectText} (${selectedItems.length} ${selectedText})`

    if (!single && alwaysShowSelectText) {
      label = selectText
    } else if (!selectedItems || selectedItems.length === 0) {
      label = selectText
    } else if (single || selectedItems.length === 1) {
      const item = selectedItems[0]
      const foundItem = this._findItem(item)
      label = this.getProp(foundItem, displayKey) || selectText
    }
    if (renderSelectText && customSelect && typeof customSelect === 'string') {
      label = customSelect
    }

    return (
      <Text
        numberOfLines={selectLabelNumberOfLines}
        style={[
          {
            flex: 1,
            fontSize: 16,
            color: colors.selectToggleTextColor
          },
          styles.selectToggleText
        ]}
      >
        {label}
      </Text>
    )
  }

  _onChangeSearchText = (searchTerm) => {
    const { onChangeSearchText } = this.props

    if (onChangeSearchText) {
      onChangeSearchText(searchTerm)
    }

    this.setState({ searchTerm })
  }

  _filterItems = (searchTerm) => {
    const { items, subKey, uniqueKey, displayKey, filterItems } = this.props

    if (filterItems) {
      return filterItems(searchTerm, items, this.props)
    }
    let filteredItems = []
    let newFilteredItems = []

    items &&
      items.forEach((item) => {
        const parts = searchTerm
          .replace(/[\^$\\.*+?()[\]{}|]/g, '\\$&')
          .trim()
          .split(' ')
        const regex = new RegExp(`(${parts.join('|')})`, 'i')

        if (regex.test(this.getProp(item, displayKey))) {
          filteredItems.push(item)
        }
        if (item[subKey]) {
          const newItem = Object.assign({}, item)
          newItem[subKey] = []
          item[subKey].forEach((sub) => {
            if (regex.test(this.getProp(sub, displayKey))) {
              newItem[subKey] = [...newItem[subKey], sub]
              newFilteredItems = this.rejectProp(
                filteredItems,
                (singleItem) => item[uniqueKey] !== singleItem[uniqueKey]
              )
              newFilteredItems.push(newItem)
              filteredItems = newFilteredItems
            }
          })
        }
      })

    return filteredItems
  }

  _removeItem = (item) => {
    const {
      uniqueKey,
      selectedItems,
      onSelectedItemsChange,
      highlightChildren,
      onSelectedItemObjectsChange
    } = this.props

    const newItems = this.rejectProp(
      selectedItems,
      (singleItem) => item[uniqueKey] !== singleItem
    )

    highlightChildren && this._unHighlightChildren(item[uniqueKey])
    onSelectedItemObjectsChange && this._broadcastItemObjects(newItems)

    // broadcast new selected items state to parent component
    onSelectedItemsChange(newItems)
  }

  _removeAllItems = () => {
    const { onSelectedItemsChange, onSelectedItemObjectsChange } = this.props
    // broadcast new selected items state to parent component
    onSelectedItemsChange([])
    this.setState({ highlightedChildren: [] })
    onSelectedItemObjectsChange && this._broadcastItemObjects([])
  }

  _selectAllItems = () => {
    const {
      items,
      uniqueKey,
      subKey,
      onSelectedItemsChange,
      onSelectedItemObjectsChange,
      readOnlyHeadings
    } = this.props

    let newItems = []
    items &&
      items.forEach((item) => {
        if (!readOnlyHeadings) {
          newItems = [...newItems, item[uniqueKey]]
        }
        Array.isArray(item[subKey]) &&
          item[subKey].forEach((sub) => {
            newItems = [...newItems, sub[uniqueKey]]
          })
      })

    onSelectedItemsChange(newItems)
    onSelectedItemObjectsChange && onSelectedItemObjectsChange(items)
  }

  // _removeItems = (items) => {
  //   const {
  //     uniqueKey,
  //     selectedItems,
  //     onSelectedItemsChange,
  //     highlightChildren,
  //     onSelectedItemObjectsChange,
  //   } = this.props

  //   const newItems = selectedItems
  //   const filtered = newItems.filter( el => !items.includes( el ) );

  //   if (highlightChildren) {
  //     // wut
  //     items.forEach((item) => {
  //       this._unHighlightChildren(item)
  //     })
  //     filtered.forEach((item) => {
  //       this._highlightChildren(item)
  //     })
  //   }
  //   onSelectedItemObjectsChange && this._broadcastItemObjects(filtered)

  //   // broadcast new selected items state to parent component
  //   onSelectedItemsChange(filtered)
  // }

  _toggleSelector = () => {
    const { onToggleSelector } = this.props
    const newState = !this.state.selector
    this.setState({
      selector: newState
    })
    onToggleSelector && onToggleSelector(newState)
  }

  _closeSelector = () => {
    const { onToggleSelector } = this.props
    this.setState({
      selector: false,
      searchTerm: ''
    })
    onToggleSelector && onToggleSelector(false)
  }
  _submitSelection = () => {
    const { onConfirm } = this.props
    this._toggleSelector()
    // reset searchTerm
    this.setState({ searchTerm: '' })
    onConfirm && onConfirm()
  }

  _cancelSelection = () => {
    const { onCancel } = this.props
    // this._removeAllItems()
    this._toggleSelector()
    this.setState({ searchTerm: '' })
    onCancel && onCancel()
  }

  _itemSelected = (item) => {
    const { uniqueKey, selectedItems } = this.props
    return selectedItems.includes(item[uniqueKey])
  }

  _toggleItem = (item, hasChildren) => {
    const {
      single,
      uniqueKey,
      selectedItems,
      onSelectedItemsChange,
      selectChildren,
      highlightChildren,
      onSelectedItemObjectsChange
    } = this.props

    if (single) {
      this._submitSelection()
      onSelectedItemsChange([item[uniqueKey]])
      onSelectedItemObjectsChange &&
        this._broadcastItemObjects([item[uniqueKey]])
    } else {
      const selected = this._itemSelected(item)
      let newItems = []
      if (selected) {
        if (hasChildren) {
          if (selectChildren) {
            newItems = [...this._rejectChildren(item[uniqueKey])]

            newItems = this.rejectProp(
              newItems,
              (singleItem) => item[uniqueKey] !== singleItem
            )
          } else if (highlightChildren) {
            this._unHighlightChildren(item[uniqueKey])
            newItems = this.rejectProp(
              selectedItems,
              (singleItem) => item[uniqueKey] !== singleItem
            )
          } else {
            newItems = this.rejectProp(
              selectedItems,
              (singleItem) => item[uniqueKey] !== singleItem
            )
          }
        } else {
          newItems = this.rejectProp(
            selectedItems,
            (singleItem) => item[uniqueKey] !== singleItem
          )
        }
      } else {
        newItems = [...selectedItems, item[uniqueKey]]

        if (hasChildren) {
          if (selectChildren) {
            newItems = [...newItems, ...this._selectChildren(item[uniqueKey])]
          } else if (highlightChildren) {
            this._highlightChildren(item[uniqueKey])
          }
        }
      }
      // broadcast new selected items state to parent component
      onSelectedItemsChange(newItems)
      onSelectedItemObjectsChange && this._broadcastItemObjects(newItems)
    }
  }

  _findItem = (id) => {
    const { items } = this.props
    return this.find(id, items)
  }

  _highlightChildren = (id) => {
    const { items, uniqueKey, subKey } = this.props
    const { highlightedChildren } = this.state
    const highlighted = [...highlightedChildren]
    if (!items) {
      return
    }
    let i = 0
    for (; i < items.length; i += 1) {
      if (items[i][uniqueKey] === id && Array.isArray(items[i][subKey])) {
        items[i][subKey].forEach((sub) => {
          !highlighted.includes(sub[uniqueKey]) &&
            highlighted.push(sub[uniqueKey])
        })
      }
    }
    this.setState({ highlightedChildren: highlighted })
  }

  _unHighlightChildren = (id) => {
    const { items, uniqueKey, subKey } = this.props
    const { highlightedChildren } = this.state
    const highlighted = [...highlightedChildren]

    const array = items.filter((item) => item[uniqueKey] === id)

    if (!array['0']) {
      return
    }
    if (array['0'] && !array['0'][subKey]) {
      return
    }

    const newHighlighted = this.reduceSelected(array['0'][subKey], highlighted)

    this.setState({ highlightedChildren: newHighlighted })
  }

  _selectChildren = (id) => {
    const { items, selectedItems, uniqueKey, subKey } = this.props
    if (!items) {
      return
    }
    let i = 0
    const selected = []
    for (; i < items.length; i += 1) {
      if (items[i][uniqueKey] === id && Array.isArray(items[i][subKey])) {
        items[i][subKey].forEach((sub) => {
          !selectedItems.includes(sub[uniqueKey]) &&
            selected.push(sub[uniqueKey])
        })
      }
    }

    // so we have them in state for SubRowItem should update checks
    this._highlightChildren(id)
    return selected
  }

  _rejectChildren = (id) => {
    const { items, selectedItems, uniqueKey, subKey } = this.props
    const arrayOfChildren = items.filter((item) => item[uniqueKey] === id)
    const selected = [...selectedItems]
    if (!arrayOfChildren['0']) {
      return
    }
    if (arrayOfChildren['0'] && !arrayOfChildren['0'][subKey]) {
      return
    }

    const newSelected = this.reduceSelected(
      arrayOfChildren['0'][subKey],
      selected
    )

    // update state for SubRowItem component should update checks
    this._unHighlightChildren(id)
    return newSelected
  }

  _toggleChildren = (item) => {
    const { uniqueKey, onSelectedItemsChange, onSelectedItemObjectsChange } =
      this.props

    const selected = this._itemSelected(item)
    let newItems = []
    if (selected) {
      newItems = [...this._rejectChildren(item[uniqueKey])]
      newItems = this.rejectProp(
        newItems,
        (singleItem) => item[uniqueKey] !== singleItem
      )
    } else {
      newItems = [...newItems, ...this._selectChildren(item[uniqueKey])]
    }

    onSelectedItemsChange(newItems)
    onSelectedItemObjectsChange && this._broadcastItemObjects(newItems)
  }

  _getSearchTerm = () => this.state.searchTerm

  // get the items back as their full objects instead of an array of ids.
  _broadcastItemObjects = (newItems) => {
    const { onSelectedItemObjectsChange, displayKey } = this.props

    const fullItems = []
    newItems.forEach((singleSelectedItem) => {
      const item = this._findItem(singleSelectedItem)
      if (!item || !item[displayKey]) {
        return
      }
      fullItems.push(item)
    })
    onSelectedItemObjectsChange(fullItems)
  }

  _customChipsRenderer = () => {
    const styles = this.getStyles(this.props.styles)
    const colors = this.getColors(this.props.colors)

    const {
      displayKey,
      items,
      selectedItems,
      subKey,
      uniqueKey,
      customChipsRenderer
    } = this.props

    return (
      customChipsRenderer &&
      customChipsRenderer({
        colors,
        displayKey,
        items,
        selectedItems,
        styles,
        subKey,
        uniqueKey
      })
    )
  }

  _renderChips = () => {
    const styles = this.getStyles(this.props.styles)
    const colors = this.getColors(this.props.colors)

    const {
      selectedItems,
      single,
      customChipsRenderer,
      showRemoveAll,
      removeAllText,
      showChips
    } = this.props
    return selectedItems.length > 0 &&
      !single &&
      showChips &&
      !customChipsRenderer ? (
      <View
        style={[
          {
            flexWrap: 'wrap',
            alignItems: 'center',
            justifyContent: 'flex-start',
            flexDirection: 'row'
          },
          styles.chipsWrapper
        ]}
      >
        {selectedItems.length > 1 && showRemoveAll ? (
          <View
            style={[
              {
                overflow: 'hidden',
                justifyContent: 'center',
                height: 34,
                borderColor: colors.chipColor,
                flexDirection: 'row',
                alignItems: 'center',
                paddingLeft: 10,
                margin: 3,
                paddingTop: 0,
                paddingRight: 10,
                paddingBottom: 0,
                borderRadius: 20,
                borderWidth: 1
              },
              styles.chipContainer
            ]}
          >
            <TouchableOpacity
              onPress={() => {
                this._removeAllItems()
              }}
              style={{
                borderTopRightRadius: 20,
                borderBottomRightRadius: 20
              }}
            >
              <Text
                style={[
                  {
                    color: colors.chipColor,
                    fontSize: 13,
                    marginRight: 0
                  },
                  styles.chipText
                ]}
              >
                {removeAllText}
              </Text>
            </TouchableOpacity>
          </View>
        ) : null}
        {this._displaySelectedItems()}
      </View>
    ) : null
  }

  _displaySelectedItems = () => {
    const {
      icons,
      uniqueKey,
      subKey,
      selectedItems,
      displayKey,
      chipRemoveIconComponent,
      parentChipsRemoveChildren,
      hideChipRemove,
      IconRenderer
    } = this.props
    const styles = this.getStyles(this.props.styles)
    const colors = this.getColors(this.props.colors)

    return selectedItems.map((singleSelectedItem) => {
      const item = this._findItem(singleSelectedItem)

      if (!item || !item[displayKey]) {
        return null
      }

      const isParent = subKey && item[subKey] && item[subKey].length

      return (
        <View
          style={[
            {
              overflow: 'hidden',
              justifyContent: 'center',
              height: 34,
              borderColor: colors.chipColor,
              borderWidth: 1,
              borderRadius: 20,
              flexDirection: 'row',
              alignItems: 'center',
              paddingLeft: 10,
              margin: 3,
              paddingTop: 0,
              paddingRight: hideChipRemove ? 10 : 0,
              paddingBottom: 0
            },
            styles.chipContainer,
            isParent && styles.parentChipContainer
          ]}
          key={item[uniqueKey]}
        >
          <Text
            numberOfLines={1}
            style={[
              {
                color: colors.chipColor,
                fontSize: 13,
                marginRight: 0
              },
              styles.chipText,
              isParent && styles.parentChipText
            ]}
          >
            {item[displayKey]}
          </Text>
          {!hideChipRemove && (
            <TouchableOpacity
              onPress={() => {
                isParent && parentChipsRemoveChildren
                  ? this._toggleChildren(item)
                  : this._toggleItem(item, isParent)
              }}
              style={{
                borderTopRightRadius: 20,
                borderBottomRightRadius: 20
              }}
            >
              {callIfFunction(chipRemoveIconComponent) || (
                <IconRenderer
                  style={[
                    {
                      color: colors.chipColor,
                      marginHorizontal: 6,
                      marginVertical: 7
                    },
                    styles.chipIcon
                  ]}
                  {...icons.close}
                />
              )}
            </TouchableOpacity>
          )}
        </View>
      )
    })
  }

  _renderSeparator = () => {
    const styles = this.getStyles(this.props.styles)
    return (
      <View
        style={[
          {
            flex: 1,
            height: StyleSheet.hairlineWidth,
            alignSelf: 'stretch',
            backgroundColor: '#dadada'
          },
          styles.separator
        ]}
      />
    )
  }

  _renderFooter = () => {
    const { footerComponent } = this.props
    return <View>{footerComponent && callIfFunction(footerComponent)}</View>
  }

  _renderItemFlatList = ({ item }) => {
    const styles = this.getStyles(this.props.styles)
    const colors = this.getColors(this.props.colors)

    // const { styles, colors } = this.state

    const { searchTerm } = this.state
    return (
      <View>
        <RowItem
          iconRenderer={this.IconRenderer}
          item={item}
          mergedStyles={styles}
          mergedColors={colors}
          itemSelected={this._itemSelected}
          searchTerm={searchTerm}
          toggleItem={this._toggleItem}
          highlightedChildren={this.state.highlightedChildren}
          {...this.props}
        />
      </View>
    )
  }
  ModalContentWrapper = ({ children }) => {
    const { modalWithSafeAreaView } = this.props
    const styles = this.getStyles(this.props.styles)

    const Component = modalWithSafeAreaView ? SafeAreaView : View
    return (
      <Component
        style={[
          { flex: 1, backgroundColor: 'rgba(0,0,0,0.5)' },
          styles.modalWrapper
        ]}
      >
        {children}
      </Component>
    )
  }
  BackdropView = ({ children, ...props }) => {
    const { modalWithTouchable } = this.props
    const Wrapper = modalWithTouchable ? TouchableWithoutFeedback : null

    return Wrapper ? (
      <Wrapper onPress={this._closeSelector}>
        <View {...props} />
      </Wrapper>
    ) : (
      <View {...props} />
    )
  }

  // _renderSubItemFlatList = ({ item }) => (
  //   <RowSubItem
  //     item={item}
  //     _toggleItem={this._toggleItem}
  //     _itemSelected={this._itemSelected}
  //     highlightedChildren={this.state.highlightedChildren}
  //     styles={styles}
  //     {...this.props}
  //   />
  // )

  render() {
    const {
      items,
      icons,
      selectedItems,
      uniqueKey,
      confirmText,
      searchPlaceholderText,
      noResultsComponent,
      loadingComponent,
      loading,
      searchTextFontFamily,
      confirmFontFamily,
      modalAnimationType,
      modalSupportedOrientations,
      hideSearch,
      hideConfirm,
      selectToggleIconComponent,
      cancelIconComponent,
      searchIconComponent,
      showCancelButton,
      hideSelect,
      headerComponent,
      searchAdornment,
      noItemsComponent,
      stickyFooterComponent,
      chipsPosition,
      autoFocus,
      disabled,
      itemsFlatListProps
    } = this.props
    const { searchTerm, selector } = this.state
    const styles = this.getStyles(this.props.styles)
    const colors = this.getColors(this.props.colors)

    const renderItems = searchTerm
      ? this._filterItems(searchTerm.trim())
      : items
    const confirmFont = confirmFontFamily.fontFamily && confirmFontFamily
    const searchTextFont =
      searchTextFontFamily.fontFamily && searchTextFontFamily
    return (
      <View>
        <Modal
          supportedOrientations={modalSupportedOrientations}
          animationType={modalAnimationType}
          transparent
          visible={selector}
          onRequestClose={this._closeSelector}
        >
          <this.ModalContentWrapper>
            <this.BackdropView
              style={[
                {
                  ...StyleSheet.absoluteFillObject,
                  zIndex: 0,
                  flex: 1
                },
                styles.backdrop
              ]}
            />

            <View
              style={[
                {
                  overflow: 'hidden',
                  marginHorizontal: 18,
                  marginVertical: 26,
                  borderRadius: 6,
                  alignSelf: 'stretch',
                  flex: 1,
                  backgroundColor: 'white'
                },
                styles.container
              ]}
            >
              {headerComponent && callIfFunction(headerComponent)}
              {!hideSearch && (
                <View
                  style={[
                    { flexDirection: 'row', paddingVertical: 5 },
                    styles.searchBar
                  ]}
                >
                  <View style={styles.center}>
                    {callIfFunction(searchIconComponent) || (
                      <this.IconRenderer
                        style={{ marginHorizontal: 15 }}
                        {...icons.search}
                      />
                    )}
                  </View>
                  <TextInput
                    value={this.state.searchTerm}
                    selectionColor={colors.searchSelectionColor}
                    onChangeText={this._onChangeSearchText}
                    placeholder={searchPlaceholderText}
                    autoFocus={autoFocus}
                    selectTextOnFocus
                    placeholderTextColor={colors.searchPlaceholderTextColor}
                    underlineColorAndroid="transparent"
                    style={[
                      {
                        flex: 1,
                        fontSize: 17,
                        paddingVertical: 8
                      },
                      searchTextFont,
                      styles.searchTextInput
                    ]}
                  />
                  {searchAdornment && searchAdornment(searchTerm)}
                </View>
              )}

              <View
                keyboardShouldPersistTaps="always"
                style={[{ paddingHorizontal: 12, flex: 1 }, styles.scrollView]}
              >
                <View>
                  {loading ? (
                    callIfFunction(loadingComponent)
                  ) : (
                    <View>
                      {!renderItems || (!renderItems.length && !searchTerm)
                        ? callIfFunction(noItemsComponent)
                        : null}
                      {items && renderItems && renderItems.length ? (
                        <View>
                          <FlatList
                            keyboardShouldPersistTaps="always"
                            removeClippedSubviews
                            initialNumToRender={15}
                            data={renderItems}
                            extraData={selectedItems}
                            keyExtractor={(item) => `${item[uniqueKey]}`}
                            ItemSeparatorComponent={this._renderSeparator}
                            ListFooterComponent={this._renderFooter}
                            renderItem={this._renderItemFlatList}
                            {...itemsFlatListProps}
                          />
                        </View>
                      ) : searchTerm ? (
                        callIfFunction(noResultsComponent)
                      ) : null}
                    </View>
                  )}
                </View>
              </View>
              <View style={{ flexDirection: 'row' }}>
                {showCancelButton && (
                  <Touchable
                    accessibilityRole="button"
                    onPress={this._cancelSelection}
                  >
                    <View
                      style={[
                        {
                          width: 54,
                          flex: Platform.OS === 'android' ? 0 : 1,
                          alignItems: 'center',
                          justifyContent: 'center',
                          paddingVertical: 8,
                          paddingHorizontal: 16,
                          borderRadius: 0,
                          flexDirection: 'row',
                          backgroundColor: colors.cancel
                        },
                        styles.cancelButton
                      ]}
                    >
                      {callIfFunction(cancelIconComponent) || (
                        <this.IconRenderer
                          style={{ color: 'white' }}
                          {...icons.cancel}
                        />
                      )}
                    </View>
                  </Touchable>
                )}
                {!hideConfirm && (
                  <Touchable
                    accessibilityRole="button"
                    onPress={this._submitSelection}
                    style={{ flex: 1 }}
                  >
                    <View
                      style={[
                        {
                          flex: Platform.OS === 'android' ? 1 : 0,
                          alignItems: 'center',
                          justifyContent: 'center',
                          paddingVertical: 8,
                          paddingHorizontal: 16,
                          borderRadius: 0,
                          flexDirection: 'row',
                          backgroundColor: colors.primary
                        },
                        styles.button
                      ]}
                    >
                      <Text
                        style={[
                          { fontSize: 18, color: '#ffffff' },
                          confirmFont,
                          styles.confirmText
                        ]}
                      >
                        {confirmText}
                      </Text>
                    </View>
                  </Touchable>
                )}
              </View>
              {stickyFooterComponent && callIfFunction(stickyFooterComponent)}
            </View>
          </this.ModalContentWrapper>
        </Modal>
        {chipsPosition === 'top' && this._renderChips()}
        {chipsPosition === 'top' && this._customChipsRenderer()}
        {!hideSelect && (
          <TouchableWithoutFeedback
            onPress={this._toggleSelector}
            disabled={this.state.selector || disabled}
          >
            <View
              style={[
                {
                  flexWrap: 'wrap',
                  flexDirection: 'row',
                  alignItems: 'center'
                },
                styles.selectToggle
              ]}
            >
              {this._getSelectLabel()}
              {callIfFunction(selectToggleIconComponent) || (
                <this.IconRenderer
                  style={{ color: colors.selectToggleTextColor }}
                  {...icons.selectArrowDown}
                />
              )}
            </View>
          </TouchableWithoutFeedback>
        )}
        {chipsPosition === 'bottom' && this._customChipsRenderer()}
        {chipsPosition === 'bottom' && this._renderChips()}
      </View>
    )
  }
}

export default SectionedMultiSelect
